  def initialize
    @jarow = FuzzyStringMatch::JaroWinkler.create( :pure )
  end
  
  
  def find_la_uri(test_string)
    test = test_string.strip.downcase
    best_score = 0
    best_match= nil
    LIST.each_key do |key|
      score = @jarow.getDistance(test,key)
      if score > best_score
        best_score = score
        best_match = key
      end
    end
    
    return LIST[best_match]
          
  end
  
  def find_la_slug(test_string)
    uri = find_la_uri(test_string)
    slug = uri.split('/').last
    return slug
  end
  
end
  

  
  
  
  
  
  